const binarySearch = (list, target) => {
    let left = 0;
    let right = list.length - 1;

    while(left <= right) {
        const midIdx = Math.floor((left + right) / 2);
        if (list[midIdx] === target) return true;
        if (list[midIdx] < target) {
            left = midIdx + 1;
        } else {
            right = midIdx - 1;
        }
    }
    return false;
}

