const isIterable = (iter) => typeof iter[Symbol.iterator] === "function";
const curry =
  (f) =>
  (a, ...args) =>
    args.length ? f(a, ...args) : (...args2) => f(a, ...args2);

const map = curry(function* (f, iter) {
  for (const a of iter) {
    yield f(a);
  }
});

const filter = curry(function* (f, iter) {
  for (const a of iter) {
    if (f(a)) yield a;
  }
});

const take = curry((length, iter) => {
  const res = [];
  for (const a of iter) {
    res.push(a);
    if (res.length === length) return res;
  }
});

const reduce = curry((f, acc, iter) => {
  if (!iter && isIterable(acc)) {
    iter = acc[Symbol.iterator]();
    acc = iter.next().value;
  }

  for (const a of iter) {
    acc = f(acc, a);
  }

  return acc;
});

const add = curry((a, b) => a + b);

const pipe =
  (...args) =>
    reduce((a, f) => f(a), args);

// Functional
pipe(
  products,
  map((a) => a.price),
  filter((p) => p < 30),
  take(3),
  reduce(add),
  console.log,
);
