class Either {
  constructor(value) {
    this.$value = value;
  }

  static right(value) {
    return new Right(value);
  }

  static left(value) {
    return new Left(value);
  }
}

class Right extends Either {
  get isRight() {
    return true;
  }

  get isLeft() {
    return false;
  }

  map(fn) {
    return new Right(fn(this.$value));
  }

  chain(fn) {
    return fn(this.$value);
  }
}

class Left extends Either {
  get isRight() {
    return false;
  }

  get isLeft() {
    return true;
  }

  map(fn) {
    return this;
  }

  chain(fn) {
    return this;
  }
}

const either = curry((l, r, e) => {
  return e.isLeft ? l(e.$value) : r(e.$value);
});

pipe(
  find((p) => p.id === "candy"),
