// lower bound: >= target
function lowerBound(list, target) {
    // 모든 원소가 target보다 작을때에는 n + 1 을 출력해야하므로 처음 구간을 잡을때 [0, n] 을 잡는다.
  let left = 0;
  let right = list.length; 

  while (left < right) {
    const m = Math.floor((left + right) / 2);

        // list[m] >= target 이면서 list[m-1] < target 를 만족하는 최소 m을 찾는 문제
        if (list[m] >= target) {            
